import { useState } from 'react';

const zones = [
  {
    id: 1,
    left: '10%',
    top: '20%',
    video: 'video1.mp4'
  },
  {
    id: 2,
    left: '30%',
    top: '50%',
    video: 'video2.mp4'
  }
];

function App() {

  const [videoActive, setVideoActive] = useState(null);

  const handleZoneClick = (videoId) => {
    setVideoActive(videoId);
  }

  return (
    <ImageInteractive
      videoActive={videoActive}
      onZoneClick={handleZoneClick}
    />
  );

}

function ImageInteractive({videoActive, onZoneClick}) {

  return (
    <div>
      <img
        src="image.png"
        zones={zones}
      />

      {videoActive && (
        <VideoPlayer videoId={videoActive}/>
      )}

    </div>
  )

}

ReactDOM.render(<App />, document.getElementById('app'))
