import React, { useEffect, useRef } from 'react';

function VideoComponent({ videoSource }) {
  const videoRef = useRef(null);

  useEffect(() => {
    const handleResize = () => {
      // Manipulations de redimensionnement ici (si nécessaire)
    };

    // Ajouter un écouteur de redimensionnement si nécessaire
    window.addEventListener('resize', handleResize);

    // Nettoyer l'écouteur de redimensionnement lors du démontage du composant
    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []); // Assurez-vous de passer les dépendances correctes à useEffect

  return (
    <div>
      <video controls autoPlay width={500} ref={videoRef}>
        <source src={videoSource} type="video/mp4" />
        Your browser does not support the video tag.
      </video>
    </div>
  );
}

function ImageComponent({ onImageClick }) {
  return (
    <div onClick={onImageClick}>
      <img src="bureau.jpg" alt="Image Interactive" id="interactiveImage" />
    </div>
  );
}

function App() {
  const [videoVisible, setVideoVisible] = React.useState(false);

  const handleImageClick = () => {
    // Afficher la vidéo uniquement si elle n'est pas déjà visible
    if (!videoVisible) {
      setVideoVisible(true);
    }
  };

  const handleZoneClick = () => {
    // Logique spécifique à la zone1 (peut être différente selon vos besoins)
    // Afficher la vidéo au clic sur zone1
    setVideoVisible(true);
  };

  const returnFromVideo = () => {
    // Cacher la vidéo
    setVideoVisible(false);
  };

  return (
    <>
      {!videoVisible && (
        <div>
          <ImageComponent onImageClick={handleImageClick} />
          <button id="zone1" className="interactiveZone" onClick={handleZoneClick}></button>
        </div>
      )}
      {videoVisible && (
        <div className="video-container">
          <VideoComponent videoSource="fire.mp4" />
          <button className="back-button" onClick={returnFromVideo}>
            Retour
          </button>
        </div>
      )}
    </>
  );
}

export default App;
