import React from 'react';
import Slider from 'react-slick';


function App() {
  const [videoVisible, setVideoVisible] = React.useState(false);

  const handleImageClick = () => {
    // Afficher la vidéo uniquement si elle n'est pas déjà visible
    if (!videoVisible) {
      setVideoVisible(true);
    }
  };

  const handleZoneClick = () => {
    // Logique spécifique à la zone1 (peut être différente selon vos besoins)
    // Afficher la vidéo au clic sur zone1
    setVideoVisible(true);
  };

  const returnFromVideo = () => {
    // Cacher la vidéo
    setVideoVisible(false);
  }

  return (
    <>
      {!videoVisible && <div>
        <ImageComponent onImageClick={handleImageClick} />
        <button id="zone1" className="interactiveZone" onClick={handleZoneClick}></button>
      </div> }
      {videoVisible && <div className="video-conta  iner">
        <VideoComponent videoSource="fire.mp4" />
        <button className="back-button" onClick={returnFromVideo}>Retour</button>
      </div>}
    </>
  );
}

// Composant de carrousel
const Carousel = () => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
  };

  return (
    <Slider {...settings}>
      <div>
        <img src="bureau.jpg" alt="Image 1" />
      </div>
      <div>
        <img src="bureau.jpg" alt="Image 2" />
      </div>
      <div>
        <img src="bureau.jpg" alt="Image 3" />
      </div>
      {/* Ajoutez autant d'images que nécessaire */}
    </Slider>
  );
};

// Montez le composant dans l'élément avec l'id "carousel"
const carouselElement = document.getElementById('carousel');
ReactDOM.render(<Carousel />, carouselElement);

export default App;
