// app.js
function VideoComponent({ videoSource }) {
  return React.createElement('div', {
      style: { display: 'none' },
      children: React.createElement('video', {
          controls: true,
          width: 500,
          children: React.createElement('source', {
              src: videoSource,
              type: 'video/mp4',
          }),
      }),
  });
}

function ImageComponent({ onImageClick }) {
  return React.createElement('div', {
      onClick: onImageClick,
      children: React.createElement('img', {
          src: 'bureau.jpg',
          alt: 'Image Interactive',
          id: 'interactiveImage',
      }),
  });
}

function App() {
  const [videoVisible, setVideoVisible] = React.useState(false);

  const handleImageClick = () => {
      // Vous pouvez ajouter d'autres logiques ici si nécessaire
      setVideoVisible(true);
  };

  const handleZoneClick = () => {
      // Logique spécifique à la zone1 (peut être différente selon vos besoins)
      alert('Clic sur la zone1 !');
      // Si vous voulez afficher la vidéo au clic sur zone1, vous pouvez décommenter la ligne suivante
      // setVideoVisible(true);
  };

  return React.createElement('div', null,
      React.createElement(ImageComponent, { onImageClick: handleImageClick }),
      React.createElement('button', {
          id: 'zone1',
          className: 'interactiveZone',
          onClick: handleZoneClick, // Ajout de la logique pour le clic sur la zone1
      }),
      videoVisible && React.createElement(VideoComponent, { videoSource: 'fire.mp4' })
  );
}

ReactDOM.render(React.createElement(App), document.getElementById('root'));
